script:
frappe.ui.form.on('Quotation', {
	validate(frm) {
        var total = 0;
        $.each(frm.doc.items || [], function (i, d) {
        console.log("Freight:", d.unit_freight);
        if (d.unit_freight) {
            total = total + (d.qty * d.unit_freight);
        }
        total = total.toFixed(2); // Set recurring decimal limit
        console.log("Total Value", total);
        });
        var charges = frm.add_child("taxes");
        frappe.model.set_value(charges.doctype,charges.name,"charge_type","Actual");
        frappe.model.set_value(charges.doctype,charges.name,"account_head","Freight and Forwarding Charges - IEPL");
        frappe.model.set_value(charges.doctype,charges.name,"description","Unit Freight Computed");
        frappe.model.set_value(charges.doctype,charges.name,"tax_amount",total);
        cur_frm.refresh_field("taxes");
    }
})
